import binascii
import struct

path = "/home/strat0m/Python/Test.txt"

def file_to_bits():
    bin_data = open(path, 'rb').read()
    bits = bin(int(binascii.hexlify(bin_data), 16))[2:]
    return bits.zfill(8 * ((len(bits) + 7) // 8))

print(file_to_bits())



#bits = ""


bits = file_to_bits()
bytes = bits.encode(encoding='UTF-8')
with open("test3.txt", "wb") as binary_file:
#    binary_file.write(struct.pack('i', int(bits[::-1], 2)))
    # Write bytes to file
    binary_file.write(bytes)

